var updateMiniBasket = function ( json, total, parent ){ 

	$.notify({
		message: '<i class="fa fa-check-circle"></i> ' + json['success'] + '<button type="button" class="close" data-dismiss="alert">&times;</button></div>'
	},{
		type: 'minimalist',
		delay: 2000,
		icon_type: 'image',
		template: '<div data-notify="container" class="col-xs-11 col-sm-3 alert alert-{0}" role="alert">' +
			'<i class="fa fa-shopping-bag fa-3 pull-left"></i>' +
			'<span data-notify="message">{2}</span>' +
		'</div>'
	});

	var mobile_display = window.getComputedStyle( $( '.pav-mobile-nav' ).get( 0 ), null).getPropertyValue( 'display' );

	if (typeof $( '.mini-cart' )[2] == "undefined" || $( '.mini-cart' )[2] == null) {
		var style = $( '.mini-cart' )[1].dataset.style;
		var heading = $( '.mini-cart' )[1].dataset.head;
	} else {
		var style = $( '.mini-cart' )[2].dataset.style;
		var heading = $( '.mini-cart' )[2].dataset.head;
	}

	switch( style ){
		case 'style-1':
			string = '<span class="cart-icon">'
		        + '<i class="fa fa-shopping-cart"></i>' 
		        + '<span class="cart-number">'+total.split(/\s+/)[0]+'</span>'
		    	+ '</span>'
		    	+ '<span class="cart-inner">'
		      	+ '<span class="cart-head"></span>'
		      	+ '<span class="pav-cart-total">'+ total.split('-')[1] +'</span>'
		    	+ '</span>';

		    	break;
		case 'style-2':  
			string = '<span class="cart-icon pull-right">'
		        + '<i class="fa fa-shopping-cart"></i>' 
		        + '<span class="cart-number">'+total.split(/\s+/)[0]+'</span>'
		    	+ '</span>'
		    	+ '<span class="cart-inner hide">'
		      	+ '<span class="cart-head">'+heading+'</span>'
		      	+ '<span class="cart-total">'+ total.split('-')[1] +'</span>'
		    	+ '</span>';

		    	break;
		case 'style-3' : 
			string = '<span class="cart-icon pull-right">'
		        + '<i class="fa fa-shopping-cart"></i>' 
		        + '<span class="cart-number">'+total.split(/\s+/)[0]+'</span>'
		    	+ '</span>'
		    	+ '<span class="cart-inner hide">'
		      	+ '<span class="cart-head">'+heading+'</span>'
		      	+ '<span class="cart-total">'+ total.split('-')[1] +'</span>'
		    	+ '</span>';
				break;
		default : 
			string = '<i class="fa fa-shopping-cart"></i>'
				+ ' <span class="cart-inner"> '
      			+ ' <span class="cart-head">' + heading + '</span> '
      			+ ' <span class="cart-total">' + total + '</span> '
    			+ ' </span> ';
		;
	}
	setTimeout(function () {
		$('.mini-cart-clone > button').html( string );
		$('.mini-cart > button').html( string );
		if( parent ){  
			$('.mini-cart > button', parent ).html( string );
			$('.mini-cart-clone > button', parent ).html( string );
		}
	}, 100);
}
