// Autocomplete */
(function($) {
	$.fn.autocomplete = function(option) {
		return this.each(function() {
			this.timer = null;
			this.items = new Array();

			$.extend(this, option);

			$(this).attr('autocomplete', 'off');

			// Focus
			$(this).on('focus', function() {
				this.request();
			});

			// Blur
			$(this).on('blur', function() {
				setTimeout(function(object) {
					object.hide();
				}, 200, this);
			});

			// Keydown
			$(this).on('keydown', function(event) {
				switch(event.keyCode) {
					case 27: // escape
						this.hide();
						break;
					default:
						this.request();
						break;
				}
			});

			// Click
			this.click = function(event) {
				event.preventDefault();

				value = $(event.target).parent().attr('data-value');

				if (value && this.items[value]) {
					this.select(this.items[value]);
				}
			}

			// Show
			this.show = function() {
				var pos = $(this).position();

				$(this).siblings('ul.dropdown-menu').css({
					top: pos.top + $(this).outerHeight(),
					left: pos.left
				});

				$(this).siblings('ul.dropdown-menu').show();
			}

			// Hide
			this.hide = function() {
				$(this).siblings('ul.dropdown-menu').hide();
			}

			// Request
			this.request = function() {
				clearTimeout(this.timer);

				this.timer = setTimeout(function(object) {
					object.source($(object).val(), $.proxy(object.response, object));
				}, 200, this);
			}

			// Response
			this.response = function(json) {
				html = '';

				if (json.length) {
					for (i = 0; i < json.length; i++) {
						this.items[json[i]['value']] = json[i];
					}

					for (i = 0; i < json.length; i++) {
						if (!json[i]['category']) {
							html += '<li data-value="' + json[i]['value'] + '"><a href="#">' + json[i]['label'] + '</a></li>';
						}
					}

					// Get all the ones with a categories
					var category = new Array();

					for (i = 0; i < json.length; i++) {
						if (json[i]['category']) {
							if (!category[json[i]['category']]) {
								category[json[i]['category']] = new Array();
								category[json[i]['category']]['name'] = json[i]['category'];
								category[json[i]['category']]['item'] = new Array();
							}

							category[json[i]['category']]['item'].push(json[i]);
						}
					}

					for (i in category) {
						html += '<li class="dropdown-header">' + category[i]['name'] + '</li>';

						for (j = 0; j < category[i]['item'].length; j++) {
							html += '<li data-value="' + category[i]['item'][j]['value'] + '"><a href="#">&nbsp;&nbsp;&nbsp;' + category[i]['item'][j]['label'] + '</a></li>';
						}
					}
				}

				if (html) {
					this.show();
				} else {
					this.hide();
				}

				$(this).siblings('ul.dropdown-menu').html(html);
			}

			$(this).after('<ul class="dropdown-menu"></ul>');
			$(this).siblings('ul.dropdown-menu').delegate('a', 'click', $.proxy(this.click, this));

		});
	}
})(window.jQuery);

(function($) {
	$.fn.pavRating = function( options ) {
		var inited = [];
		var options = $.extend( {}, {
			maxStar 	: 5
		}, options);
		return this.each( function() {

			var self = this;
			var ratingWrapper = false;

			init( self );

			function init( el ) {
				var parent = $( el ).parent();

				if ( inited.indexOf( parent ) !== -1 || parent.find( '.rating-wrapper' ).length > 0 ) {
					return;
				}
				parent.find( '*:not(.control-label)' ).addClass( 'hide' );
				inited.push( parent );

				var html = '<div class="rating-wrapper">';
				for ( var i = 1; i <= options.maxStar; i++ ) {
					html += '<span class="fa fa-stack" data-star="'+i+'">';
					html += '<i class="fa fa-star fa-stack-1x"></i>';
					html += '<i class="fa fa-star-o fa-stack-1x"></i>';
					html += '</span>';
				}
				html += '</div>';
				parent.find( $( el ) ).before( html );
				parent.find( '.rating-wrapper' ).css( { display: 'inline-block' } );

				var rating = ratingWrapper = $( '.rating-wrapper' );

				rating.mousemove( function( e ) {
					// mouse enter and move
					var choosed = rating.find( '.selected' );
					choosed = choosed.length == 1 ? choosed.data( 'star' ) : false;
					var ratingAxis = self.getRatingsAxis();
					var mouseAxis = self.getMouseAxis( e );
					var itemWidth = Math.ceil( ratingAxis.width / 5 );
					var currentStar = Math.ceil( ( mouseAxis.pageX - ratingAxis.pageX ) / itemWidth );
					currentStar = currentStar ? currentStar : 1;
					for ( var i = 1; i <= 5; i++ ) {
						var index = i - 1;
						var span = $( parent.find( '.fa-stack' ).get( index ) );
						if ( i <= currentStar ) {
							span.addClass( 'active' );
						} else {
							if ( ! choosed || i > choosed ) {
								span.removeClass( 'active' ).removeClass( 'selected' );
							}
						}
					}
				}).mouseleave( function( e ) {
					// mouse leave rating wrap
					var choosed = rating.find( '.selected' );
					var val = choosed.data( 'star' );
					if ( choosed.length == 0 ) {
						rating.find( '.fa-stack' ).removeClass( 'active' );
					} else {
						for ( var i = 1; i <= 5; i++ ) {
							var index = i - 1;
							var span = $( parent.find( '.fa-stack' ).get( index ) );
							if ( i <= val ) {
								span.addClass( 'active' );
							} else {
								span.removeClass( 'active' ).removeClass( 'selected' );
							}
						}
					}

				}).mousedown( function( e ) {
					// mouse down select rating value
					var ratingAxis = self.getRatingsAxis();
					var mouseAxis = self.getMouseAxis( e );
					var itemWidth = Math.ceil( ratingAxis.width / 5 );
					var currentStar = Math.ceil( ( mouseAxis.pageX - ratingAxis.pageX ) / itemWidth );
					currentStar = currentStar ? currentStar : 1;
					for ( var i = 1; i <= 5; i++ ) {
						var index = i - 1;
						var span = $( parent.find( '.fa-stack' ).get( index ) );
						if ( i < currentStar ) {
							span.addClass( 'active' ).removeClass( 'selected' );
						} else if ( i == currentStar ) {
							span.addClass( 'selected' );
							$( 'input[value="'+currentStar+'"]' ).attr( 'checked', true );
						} else {
							span.removeClass( 'active' ).removeClass( 'selected' );
						}
					}
  
				} );
			}

			this.getRatingsAxis = function() {
				var ratingWidth = ratingWrapper.get( 0 ).getBoundingClientRect().width;
				return {
					width: ratingWidth,
					pageX: ratingWrapper.offset().left,
					pageY: ratingWrapper.offset().top,
				};
			}

			/**
			 * mouse axis
			 */
			this.getMouseAxis = function( event ) {
				return {
					pageX: event.pageX,
					pageY: event.pageY
				};
			}
		} );
	}
})(window.jQuery);
